import { Browser, By } from '@progress/kendo-e2e';
import { LOCATORS } from '../../../e2eUtils/const';

let browser: Browser;

beforeAll(() => {
  browser = new Browser();
});

beforeEach(async () => {
  await browser.navigateTo('http://localhost:3000/contextmenu/overview');
});

afterEach(async () => {
  expect(await browser.getErrorLogs()).toEqual([]);
});

afterAll(async () => {
  await browser.close();
});

it('should not have errors', async () => {
  await browser.contextClick(By.css('.target'));
  expect(await browser.isVisible(LOCATORS.CONTEXT_MENU)).toBe(true);
});
