import { Browser, By } from '@progress/kendo-e2e';

let browser: Browser;

beforeAll(() => {
  browser = new Browser();
});

beforeEach(async () => {
  await browser.navigateTo('http://localhost:3000/expansionpanel/overview');
});

afterEach(async () => {
  expect(await browser.getErrorLogs()).toEqual([]);
});

afterAll(async () => {
  await browser.close();
});

it('should not have errors', async () => {
  expect(await browser.isVisible(By.css('.k-expander'))).toBe(true);
});
